import { useEffect, useState } from "react";
import { Modal } from "../modalComponent/modalComponent.tsx";
import React from "react";
import FoodOption from "../foodOption/foodOption.tsx";
import BigMac from "../imgs/foods/bigmac.webp"

interface Props {
  isOpen: boolean;
  onClose: () => void;
  children: React.ReactNode;
}

const RestaurantModal: React.FC<Props> = ({ isOpen, onClose, children }) => {
  const [modalTitle, setModalTitle] = useState("");

  useEffect(() => {
    if (typeof children === "string") {
      setModalTitle(children);
    }
  }, [children]);

  return (
    <div>
      <Modal open={isOpen} onClose={onClose} title={children?.toString()}>
      {children}
      </Modal>
    </div>
  );
};

export default RestaurantModal;
