import React, { useState } from "react";
import HeaderComponent from "../../components/header/Header";
import { FoodContainer } from "../../components/foodcontainer/foodContainer";
import PizzaHut from '../../components/imgs/restaurants/pizzahut.webp';
import BurgerKing from '../../components/imgs/restaurants/burgerking.webp';
import McDonalds from '../../components/imgs/restaurants/mcdonalds.webp';
import RestaurantModal from "../../components/restaurantModal/restaurantModal";

import './Home.scss'

function Home() {
  const [modalOpen, setModalOpen] = useState(false);

  const openModal = () => {
    setModalOpen(true);
  };

  const closeModal = () => {
    setModalOpen(false);
  };

  return (
    <div className="app">
      <HeaderComponent />
      <h1>Popular</h1>

      <RestaurantModal isOpen={modalOpen} onClose={closeModal} />

      <div className="foods">
        <div className="grid">
          <FoodContainer
            image={PizzaHut}
            children="Pizza Hut"
            onButtonClick={openModal}
          />

          <FoodContainer
            image={BurgerKing}
            children="Burger King"
            onButtonClick={openModal}
          />

          <FoodContainer
            image={McDonalds}
            children="McDonald's"
            onButtonClick={openModal}
          />
        </div>
      </div>
    </div>
  );
}

export default Home;
