import React, { useState } from 'react';
import logo from '../imgs/Logo.png';
import cart from '../imgs/cart.png';
import './Header.css';
import { Link } from 'react-router-dom';
import { ButtonComponent } from 'components/buttonComponent/button';
import { Modal } from './Modal'; // Importe o componente Modal

const HeaderComponent = () => {
  const [modalOpen, setModalOpen] = useState(false); // Estado para controlar a abertura do modal

  const handleModalOpen = () => {
    setModalOpen(true); // Função para abrir o modal
  };

  return (
    <header className="page-header">
      <div className="logo">
        <Link className="links" to="/"><img src={logo} alt="Munchie logo" /></Link>
      </div>
      <nav>
        <ul>
          <Link className="links" to="/"><li>Home</li> </Link>
          <Link className="links" to="/categories"><li>Categories </li></Link>
          <Link className="links" to="/restaurants"><li>Restaurants </li></Link>
          <Link className="links" to="/aboutus"><li>About us</li></Link>
        </ul>
      </nav>

      <div className="checkout-cart">
        {/* ícone do carrinho */}
        <div id="cart-icon">
          <a href="#">
            <img className="cart-img" src={cart} alt="Cart Icon" />
            <span id="cart-count"></span>
          </a>
        </div>
        <div className="checkout">
          <ButtonComponent
            type="primary"
            onClick={handleModalOpen} // Passa a função de abertura do modal como propriedade onClick
            children="Check-out Order"
            size={3}
            id={''}
          />
        </div>
      </div>

      {/* Modal */}
      <Modal
        open={modalOpen}
        onClose={() => setModalOpen(false)} // Função de callback para fechar o modal
        title="Meu Modal"
      >
        {/* Conteúdo do modal */}
        <p>Conteúdo do modal aqui...</p>
      </Modal>
    </header>
  );
};

export default HeaderComponent;
