import React from 'react';
import Card from '@mui/material/Card';
import {CardActionArea } from '@mui/material';
import { ButtonComponent } from '../buttonComponent/button';
import './foodContainer.scss'
import { useNavigate } from 'react-router-dom';


interface FoodContainerProps {
  image: string;
  onButtonClick: () => void;
  children: string;
  route: string;
}

export const FoodContainer: React.FC<FoodContainerProps> = ({
  image,
  children,
  route,
}) => {

    const history = useNavigate();

    const handleButtonClick = () => {
      history(route);
    };
  return (
    <Card className='cards-component' sx={{ maxWidth: 345 , maxHeight: 345}} style={{ backgroundImage: `url(${image})`, borderRadius: '20px'}}onClick={handleButtonClick}>
      <CardActionArea className='all'>
        <div className='button'>
        <ButtonComponent
         type={'primary'} 
         onClick={handleButtonClick}
         children={children}
         size={2} id={''} />
        </div>
      </CardActionArea>
    </Card>
  );
};
