import HeaderComponent from "../../components/header/Header";
import React, { useState }from "react";
import { FoodContainer } from "../../components/foodcontainer/foodContainer";
import Hamburger from '../../components/imgs/categories/hamburger.webp';
import Healthy from '../../components/imgs/categories/healthy.webp';
import Pasta from '../../components/imgs/categories/pasta.webp';
import Pizza from '../../components/imgs/categories/pizza.webp';
import SeaFood from '../../components/imgs/categories/seafood.webp';
import Steak from '../../components/imgs/categories/steak.webp';
import { Link, useNavigate } from 'react-router-dom';

import Outback2 from '../../components/imgs/restaurants/outback2.webp';

import RestaurantModal from "../../components/restaurantModal/restaurantModal";
import FoodOption from "../../components/foodOption/foodOption";

import './Categories.scss'

function Categories(){

    const [modalOpen, setModalOpen] = useState();

    const handleModalOpen = () => {
        setModalOpen(true);
      };

      const handleModalClose = () => {
        setModalOpen(false);
      };

    const handleButtonClick = () => {
        window.location.href = '/hamburger';
      };

    return (
        <div className="app">
        <HeaderComponent />
        <h1>Categories</h1>

        <RestaurantModal
          children="Hamburger"
          isOpen={modalOpen}
          onClose={() => handleModalClose()}
        >
          <h1 className="h1-restaurant">Hamburger</h1>
          <FoodOption
            image={Outback2}
            title={"Brazilian"}
            onCartButtonClick={() => {}}
            price="20,00$"
            cartButtonText={""}
          />
          <FoodOption
            image={Outback2}
            title={"Pepperoni"}
            onCartButtonClick={() => {}}
            price="25,00$"
            cartButtonText={""}
          />
        </RestaurantModal>

        <div className="foods">
        <div className="grid">
        <FoodContainer
        image={Hamburger}
        onButtonClick={modalOpen()} 
        children="Hamburger">
        </FoodContainer>

        <FoodContainer
        image={Healthy} 
        children="Healthy">
        </FoodContainer>

        <FoodContainer
        image={Pasta} 
        children="Pasta">
        </FoodContainer>

        <FoodContainer
        image={Pizza} 
        children="Pizza">
        </FoodContainer>

        <FoodContainer
        image={SeaFood} 
        children="Sea Food">
        </FoodContainer>

        <FoodContainer
        image={Steak} 
        children="Steak">
        </FoodContainer>

        </div>
        </div>

        </div>
    )
}

export default Categories;
