import * as React from 'react';
import Card from '@mui/material/Card';
import CardActions from '@mui/material/CardActions';
import CardContent from '@mui/material/CardContent';
import CardMedia from '@mui/material/CardMedia';
import Button from '@mui/material/Button';
import Typography from '@mui/material/Typography';
import './foodOption.scss';

interface FoodOptionProps {
  image: string;
  title: string;
  description: string;
  shareButtonText: string;
  learnMoreButtonText: string;
  onShareButtonClick: () => void;
  onLearnMoreButtonClick: () => void;
}

const FoodOption: React.FC<FoodOptionProps> = ({
  image,
  title,
  description,
  shareButtonText,
  learnMoreButtonText,
  onShareButtonClick,
  onLearnMoreButtonClick,
}) => {
  return (
    <Card className="food-option-card">
      <CardMedia className="food-photo" component="img" alt="Food" image={image} />
      <CardContent>
        <Typography gutterBottom variant="h5" component="div">
          {title}
        </Typography>
        <Typography variant="body2" color="text.secondary">
          {description}
        </Typography>
      </CardContent>
      <CardActions>
        <Button size="small" onClick={onShareButtonClick}>
          {shareButtonText}
        </Button>
        <Button size="small" onClick={onLearnMoreButtonClick}>
          {learnMoreButtonText}
        </Button>
      </CardActions>
    </Card>
  );
};

export default FoodOption;
